import React, { useState } from 'react';

interface Tag {
  name: string;
}

const TagInput: React.FC<{ tags: Tag[], onChange: (newTags: Tag[]) => void }> = ({ tags, onChange }) => {
  const [inputValue, setInputValue] = useState('');

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
  }

  const handleAddTag = () => {
    if (inputValue.trim() !== '') {
      setInputValue('');
      onChange([...tags, { name: inputValue }]);
    }
  }

  const handleRemoveTag = (index: number) => {
    onChange(tags.filter((tag, i) => i !== index));
  }

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === 'Enter') {
      event.preventDefault();
      handleAddTag();
    }
  }

  return (
    <div>
      <div className="d-flex flex-wrap">
        {tags.map((tag, index) => (
          <span className="badge badge-secondary mr-2 mb-2" key={index}>
            {tag.name}
            <button className="btn btn-link p-0" onClick={() => handleRemoveTag(index)}>
              <i className="fas fa-times"></i>
            </button>
          </span>
        ))}
      </div>
      <div className="input-group">
        <input type="text" value={inputValue} onChange={handleChange} onKeyDown={handleKeyDown} className="form-control" />
        <div className="input-group-append">
          <button className="btn btn-secondary" onClick={handleAddTag}>Add Tag</button>
        </div>
      </div>
    </div>
  );
}

export default TagInput;
