import React, { useState } from 'react';

interface Tag {
  name: string;
}

const TagInput: React.FC = () => {
  const [tags, setTags] = useState<Tag[]>([]);
  const [inputValue, setInputValue] = useState('');

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
  }

  const handleAddTag = () => {
    if (inputValue.trim() !== '') {
      setTags([...tags, { name: inputValue }]);
      setInputValue('');
    }
  }

  const handleRemoveTag = (index: number) => {
    setTags(tags.filter((tag, i) => i !== index));
  }

  return (
    <div>
      <div className="d-flex flex-wrap">
        {tags.map((tag, index) => (
          <span className="badge badge-secondary mr-2 mb-2" key={index}>
            {tag.name}
            <button className="btn btn-link p-0" onClick={() => handleRemoveTag(index)}>
              <i className="fas fa-times">x</i>
            </button>
          </span>
        ))}
      </div>
      <div className="input-group">
        <input type="text" value={inputValue} onChange={handleChange} className="form-control" />
        <div className="input-group-append">
          <button className="btn btn-secondary" onClick={handleAddTag}>Add Tag</button>
        </div>
      </div>
    </div>
  );
}

export default TagInput;
