import React, { useState } from 'react';
import TagInput from './TagInput'; // Import the TagInput component

interface Tag {
  name: string;
}

const SearchBar: React.FC = () => {
  const [tags, setTags] = useState<Tag[]>([]);
  const [what, setWhat] = useState('');
  const [selectedOption, setSelectedOption] = useState('');

  const options = ['Option 1', 'Option 2', 'Option 3']; // These are the options for the select element

  const handleTagInputChange = (newTags: Tag[]) => {
    setTags(newTags);
  }

  const handleWhatChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setWhat(event.target.value);
  }

  const handleOptionChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedOption(event.target.value);
  }

  const handleSearch = () => {
    // Perform the search using the selected option, what, and tags
    console.log(`Searching for ${what} with option ${selectedOption} and tags: ${tags.map(tag => tag.name).join(', ')}`);
  }

  return (
    <div className="form-inline my-2 my-lg-0">
      <TagInput tags={tags} onChange={handleTagInputChange} />
      <input type="text" value={what} onChange={handleWhatChange} className="form-control mr-sm-2" placeholder="What" />
      <select value={selectedOption} onChange={handleOptionChange} className="custom-select my-1 mr-sm-2">
        {options.map(option => (
          <option key={option} value={option}>{option}</option>
        ))}
      </select>
      <button className="btn btn-primary my-2 my-sm-0" onClick={handleSearch}>Search</button>
    </div>
  );
}

export default SearchBar;
