import React, { useState } from "react";

interface Tag {
  name: string;
}

const TagInput: React.FC<{
  tags: Tag[];
  onChange: (newTags: Tag[]) => void;
}> = ({ tags, onChange }) => {
  const [inputValue, setInputValue] = useState("");

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
  };

  const handleAddTag = () => {
    if (inputValue.trim() !== "") {
      setInputValue("");
      onChange([...tags, { name: inputValue }]);
    }
  };

  const handleRemoveTag = (index: number) => {
    onChange(tags.filter((tag, i) => i !== index));
  };

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleAddTag();
    }
  };

  return (
    <div>
      <div className="input-group input-group-lg">
        <input
          type="text"
          value={inputValue}
          onChange={handleChange}
          onKeyDown={handleKeyDown}
          className="form-control"
        />
        <div className="input-group-append">
          <button className="btn btn-secondary" onClick={handleAddTag}>
            Add Tag
          </button>
        </div>
      </div>
      <div className="d-flex flex-wrap">
        {tags.map((tag, index) => (
          <span>
            <span className="badge badge-pill badge-primary" style={{border:"2px solid"}} key={index}>
              {tag.name}
            </span>
            <button className="btn p-0" onClick={() => handleRemoveTag(index)}>
              x
            </button>
          </span>
        ))}
      </div>

    </div>
  );
};

export default TagInput;
