import React, { useState } from "react";

interface Tag {
  name: string;
}

const TagInput: React.FC<{
  tags: Tag[];
  onChange: (newTags: Tag[]) => void;
}> = ({ tags, onChange }) => {
  const [inputValue, setInputValue] = useState("");

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
  };

  const handleAddTag = () => {
    if (inputValue.trim() !== "") {
      setInputValue("");
      onChange([...tags, { name: inputValue }]);
    }
  };

  const handleRemoveTag = (index: number) => {
    onChange(tags.filter((tag, i) => i !== index));
  };

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === "Enter") {
      event.preventDefault();
      handleAddTag();
    }
  };

  return (
    <>
      <span
        className="input-group-text input-group-prepend"
        style={{ borderRadius: "0 0 0 0" }}
        onClick={handleAddTag}
      >
        Add Dependency
      </span>

      <input
        type="text"
        value={inputValue}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
        className="form-control"
        style={{ borderRadius: "0 30px 30px 0", maxWidth: "150px" }}
      />
      <div>
        {tags.map((tag, index) => (
          <span>
            <span
              className="badge badge-pill badge-primary"
              style={{ border: "1px solid", margin: "1px" }}
              key={index}
            >
              {tag.name}
              <button
                className="btn btn-sm p-0"
                style={{
                  marginLeft: "3px",
                  marginTop: "0",
                  marginBottom: "3px",
                }}
                onClick={() => handleRemoveTag(index)}
              >
                x
              </button>
            </span>
          </span>
        ))}
      </div>
    </>
  );
};

export default TagInput;
