import React, { useState } from 'react';

interface Tag {
  name: string;
}

const TagInput = ({onChange}:{
    inTags:Tag[], onChange:(tags:Tag[])=>void
  }) => {
  const [tags, setTags] = useState<Tag[]>([]);
  const [inputValue, setInputValue] = useState('');

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setInputValue(event.target.value);
  }

  const handleAddTag = () => {
    if (inputValue.trim() !== '') {
      const newTags = [...tags, { name: inputValue }]
      setTags(newTags);
      onChange(newTags);
      setInputValue('');
    }
  }

  const handleRemoveTag = (index: number) => {
    const newTags = tags.filter((_, i) => i !== index)
    setTags(newTags);
    onChange(newTags)
  }

  return (
    <div style={{maxWidth:'40%'}}>
      <div className="d-flex flex-wrap">
        {tags.map((tag, index) => (
          <span className="badge badge-secondary mr-2 mb-2" key={index}>
            {tag.name}
            <button className="btn btn-link" onClick={() => handleRemoveTag(index)}>
              x
            </button>
          </span>
        ))}
      </div>
      <div className="input-group">
        <input type="text" value={inputValue} onChange={handleChange} className="form-control" />
        <div className="input-group-append">
          <button className="btn btn-secondary" onClick={handleAddTag}>Add Tag</button>
        </div>
      </div>
    </div>
  );
}

export default TagInput;
